import { createSlice } from "@reduxjs/toolkit";

const eventSlice = createSlice({
  name: "event",
  initialState: {
    productId: 0,
    productImage: "",
    productName: "",
    productPrice: 0,
    productSize: 0,
    tradeInfo: [{ tradeInfoId: 0, price: 0, data: "" }],
  },
  reducers: {
    setEvent: (state, action) => {
      console.log("셋이벤트 액션", action);
      console.log("셋이벤트 스테이츠", state);
      state = action.payload;
    },
  },
});
export default eventSlice.eventReducer;
export const { setEvent } = eventSlice.actions;

