import { io } from "socket.io-client";

export const socket = io("http://localhost:3001/raffles", {
  transports: ["websocket"],
});

export const initSocketConnection = () => {
  if (socket) return socket.connect();
};

export const sendSocketMessage = (cmd, body = null) => {
  if (socket === null || socket.connected === false) {
    initSocketConnection();
  }
  socket.emit("mesage", {
    cmd,
    body,
  });
};

const cbMap = new Map();

export const socketInforReceived = (cbType, cb) => {
  cbMap.set(cbType, cb);
};

if (socket.hasListeners("message")) {
  socket.off("message");
}

socket.on("message", (ref) => {
  for (let [, cbValue] of cbMap) {
    cbValue(null, ref);
  }
});

export const disconnectSocket = () => {
  if (socket === null || socket.connected === false) {
    return;
  }
  socket.disconnect();
  socket = undefined;
};
