import * as React from "react";
import Button from "@mui/material/Button";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import CssBaseline from "@mui/material/CssBaseline";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import axios from "axios";

const theme = createTheme();

export default function Main() {
  const [itemData, setItemData] = React.useState([]);
  React.useEffect(() => {
    axios
      .get("http://localhost:3001/products")
      .then(function (response) {
        setItemData(response.data);
        console.log("메인 메이지 Itemdata", response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <main>
        <Box
          sx={{
            bgcolor: "background.paper",
            pt: 6,
            pb: 2,
          }}>
          <Container maxWidth="sm">
            <Typography
              component="h1"
              variant="h2"
              align="center"
              color="text.primary"
              gutterBottom>
              Main Page
            </Typography>
          </Container>
        </Box>
        <Container sx={{ py: 1 }} maxWidth="95%">
          <Grid container spacing={2}>
            {itemData.map((response) => (
              <Grid item key={response.ProductList} md={2}>
                <Card
                  sx={{
                    width: "100%",
                    height: "100%",
                    display: "flex",
                    flexDirection: "column",
                  }}>
                  <CardMedia
                    component="img"
                    image="https://img6.yna.co.kr/mpic/YH/2021/10/25/MYH20211025011600534_P4.jpg"
                  />
                  <CardContent sx={{ flexGrow: 1 }}>
                    <Typography gutterBottom variant="h5">
                      {response.productName}
                    </Typography>
                    <Typography>
                      {`상품사이즈 : ${response.productSize}`}
                    </Typography>
                    <Typography>
                      {`상품정발가격 : ${response.productPrice}`}
                    </Typography>
                    <Typography>
                      {`입찰시작가격 : ${response.startPrice}`}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Button size="small">입찰참여</Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>
      </main>
    </ThemeProvider>
  );
}
