import * as React from "react";
import ImageList from "@mui/material/ImageList";
import ImageListItem from "@mui/material/ImageListItem";
import ImageListItemBar from "@mui/material/ImageListItemBar";
import ListSubheader from "@mui/material/ListSubheader";
import IconButton from "@mui/material/IconButton";
import InfoIcon from "@mui/icons-material/Info";
import axios from "axios";

export default function Main() {
  axios
    .get("http://localhost:3001/products")
    .then(function (response) {
      // const itemData = response.data;
      const [itemData, setItemData] = React.useState([]);
      console.log("메인 메이지 Itemdata", itemData);

      return (
        <ImageList sx={{ width: "90%", height: "100%" }}>
          <ImageListItem key="Subheader" cols={5}>
            <ListSubheader component="div">Product List</ListSubheader>
          </ImageListItem>
          {itemData.map((item) => (
            <ImageListItem key={item.productId}>
              <img
                src={`${item.productImage}`}
                srcSet={`${item.productImage}`}
                alt={item.productName}
                loading="lazy"
              />
              <ImageListItemBar
                title={item.productName}
                subtitle={item.Price}
                actionIcon={
                  <IconButton
                    sx={{ color: "rgba(255, 255, 255, 0.54)" }}
                    aria-label={`info about ${item.productName}`}>
                    <InfoIcon />
                  </IconButton>
                }
              />
            </ImageListItem>
          ))}
        </ImageList>
      );
    })
    .catch(function (error) {
      console.log(error);
    });
}
