import React, { useState } from "react";
import { Box, Grid, Typography, TextField, Stack } from "@mui/material";
import OutlinedInput from "@mui/material/OutlinedInput";
import InputLabel from "@mui/material/InputLabel";
import InputAdornment from "@mui/material/InputAdornment";
import FormControl from "@mui/material/FormControl";
import MainCard from "../Components/MainCard";

function BidStates() {
  const [amount, setAmount] = useState("");
  amount.addEventListener("keyup", function (e) {
    let value = e.target.value;
    value = Number(value.replaceAll(",", ""));
    if (isNaN(value)) {
      amount = 0;
    } else {
      const formatValue = value.toLocaleString("ko-KR"); //[5]
      amount = formatValue;
    }
  });
  return (
    <Box sx={{ display: "flex", flexWrap: "wrap" }}>
      <Stack width="100%" sx={{ mt: 1 }}>
        <Typography variant="h5">직전 입찰금액</Typography>
        <MainCard id="exportAmount" sx={{ mt: 2 }}>
          ₩ {amount} 원
        </MainCard>
        <Typography sx={{ mt: 2 }} variant="h5">
          입찰금액입력
        </Typography>
        <FormControl fullWidth sx={{ mt: 2 }}>
          <InputLabel htmlFor="amount">Amount</InputLabel>
          <OutlinedInput
            id="amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            startAdornment={<InputAdornment position="start">₩</InputAdornment>}
            endAdornment={<InputAdornment position="end">원</InputAdornment>}
            label="Amount"
          />
        </FormControl>
      </Stack>
    </Box>
  );
}

export default BidStates;
