import { createSlice } from "@reduxjs/toolkit";

export const eventSlice = createSlice({
  name: "event",
  initialState: {
    productId: 0,
    productImage: "",
    productName: "",
    productPrice: 0,
    productSize: 0,
    tradeInfo: [{ tradeInfoId: 0, price: 0, data: "" }],
  },
  reducers: {
    setEvent: (state, action) => {
      state = action.payload;
    },
  },
});
export default eventSlice.eventReducer;
